import React, { useEffect } from 'react';
import { Link } from "react-router-dom";
import { getUserRole } from "../util/auth";
import "../css/productlist.css"; 
import classes from "../css/style.css";

function ProductList({ products }) {
    const userRole = getUserRole();

    useEffect(() => {
        const script = document.createElement('script');
        script.src = 'https://cdnjs.cloudflare.com/ajax/libs/jquery/3.6.0/jquery.min.js'; // Ajoutez le lien vers jQuery CDN
        script.async = true;
        document.body.appendChild(script);

        return () => {
            document.body.removeChild(script);
        };
    }, []);

    useEffect(() => {
        $('.carousel').carousel({
            interval: 5000
        });
    }, []);

    return (
        <div className="container">
            <h1 style={{ textDecoration: 'underline'}}>Product List</h1>
            
            <div className="row">
                {products.map((pro) => (
                    <div key={pro.id} className="col-md-4 mb-4">
                        <div className="card">
                            <img className="card-img-top" src={`data:image/png;base64,${pro.image}`} alt="" />
                            <div className="card-body">
                                <h5 className="card-title">{pro.productName}</h5>
                                <p className="card-text">Category: {pro.catName}</p>
                                <p className="card-text">Quantity: {pro.quantity}</p>
                                <p className="card-text">Price: {pro.price}</p>
                                <div className="btn-group" role="group" aria-label="Product Actions">
                                    {userRole === 'Admin' &&
                                        <>
                                            <Link to={`/editproduct/${pro.id}`} className="btn btn-primary">Edit</Link>
                                            <Link to={`/deleteproduct/${pro.id}`} className="btn btn-danger">Delete</Link>
                                        </>
                                    }
                                    <Link to={`/addToCart/${pro.id}`} className="btn btn-warning">Add To Cart</Link>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Sliding Information */}
            <section id="recommandations" style={{ width: '80%', margin: '0 auto', backgroundColor: '#f2f2f2', borderRadius: '10px', padding: '20px', display: 'flex', flexDirection: 'column', justifyContent: 'space-between', marginTop: '50px' }}>
                <div className="container">
                    <div className="red-divider"></div>
                    <div className="heading">
                        <center><h2 style={{ fontFamily: 'Arial, sans-serif', fontSize: '60px', padding: '0', marginTop: '0', background: 'linear-gradient(to right, rgb(0, 128, 113), #6ba2cf)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent' }}>Recommandations</h2></center>
                    </div>
                    <div id="myCarousel" className="carousel slide text-center" data-bs-ride="carousel">
                        <div className="carousel-inner" role="listbox">
                            <div className="carousel-item active">
                                <h3>"Le mascara que j'ai acheté ici a vraiment transformé mes cils. Il est incroyablement volumisant et tient toute la journée sans s'effriter. Je ne peux plus m'en passer!"</h3>
                                <h4>Alice, passionnée de maquillage</h4>
                            </div>
                            <div className="carousel-item">
                                <h3>"J'ai récemment essayé ce rouge à lèvres et je suis époustouflée par sa tenue. Il ne s'efface pas, même après avoir mangé et bu. De plus, la couleur est magnifique!"</h3>
                                <h4>Sophie, amatrice de rouges à lèvres</h4>
                            </div>
                            <div className="carousel-item">
                                <h3>"Ce fond de teint est tout simplement parfait pour ma peau. Il offre une couvrance naturelle et légère tout en cachant efficacement les imperfections. Je le recommande vivement!"</h3>
                                <h4>Emma, adepte du teint parfait</h4>
                            </div>
                            <div className="carousel-item">
                                <h3>"Je suis tombée amoureuse de cette palette d'ombres à paupières dès la première utilisation. Les couleurs sont superbes et la pigmentation est incroyable. Je peux créer une multitude de looks avec une seule palette!"</h3>
                                <h4>Laura, passionnée de maquillage artistique</h4>
                            </div>
                        </div>
                        {/* Ajoutez les flèches de navigation */}
                        <button className="carousel-control-prev" type="button" data-bs-target="#myCarousel" data-bs-slide="prev">
                            <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                            <span className="visually-hidden">Previous</span>
                        </button>
                        <button className="carousel-control-next" type="button" data-bs-target="#myCarousel" data-bs-slide="next">
                            <span className="carousel-control-next-icon" aria-hidden="true"></span>
                            <span className="visually-hidden">Next</span>
                        </button>
                    </div>
                </div>
            </section>
        </div>
    );
}

export default ProductList;
